package com.tsing.product.api.dict.common.impl;import com.tsing.global.utils.ObjectMappingUtil;import com.tsing.product.api.dict.bo.request.ProductDictSaveRequest;import com.tsing.product.api.dict.bo.request.ProductDictUpdateRequest;import com.tsing.product.api.dict.common.ProductDictCommonService;import com.tsing.product.api.dict.entity.ProductDictEntity;import com.tsing.product.api.dict.mapper.ProductDictMapper;import org.springframework.stereotype.Service;import javax.annotation.Resource;/** * @author Tsing * @version 0.0.1 * @date 2021/8/5 6:13 上午 * @description: 产品字典服务核心处理层 */@Servicepublic class ProductDictCommonServiceImpl implements ProductDictCommonService {    @Resource    private ProductDictMapper productDictMapper;    @Override    public ProductDictEntity coverProductDictSaveRequestToEntity(ProductDictSaveRequest param) {        return ObjectMappingUtil.map(param, ProductDictEntity.class);    }    @Override    public Boolean save(ProductDictEntity entity) {        return productDictMapper.insert(entity) > 0;    }    @Override    public ProductDictEntity coverProductDictUpdateRequestToEntity(ProductDictUpdateRequest param) {        return ObjectMappingUtil.map(param, ProductDictEntity.class);    }    @Override    public Boolean modify(ProductDictEntity param) {        Long id = param.getId();        String categoryCode = param.getCategoryCode();        String dictCode = param.getDictCode();        ProductDictEntity entity = productDictMapper.selectById(id);        if(entity.getCategoryCode().equals(categoryCode) && entity.getDictCode().equals(dictCode)) {            return false;        }        return productDictMapper.updateById(entity) > 0;    }}