package com.tsing.product.api.teambuilding.common.impl;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.tsing.global.utils.ObjectMappingUtil;import com.tsing.product.api.teambuilding.bo.reponse.TeambuildingBoEntity;import com.tsing.product.api.teambuilding.bo.request.TeambildingPage;import com.tsing.product.api.teambuilding.common.TeambuildingCommonService;import com.tsing.product.api.teambuilding.entity.TeambuildingEntity;import com.tsing.product.api.teambuilding.mapper.TeambuildingMapper;import org.springframework.stereotype.Service;import javax.annotation.Resource;/** * @author Tsing * @version 0.0.1 * @date 2021/8/1 1:14 下午 * @description: 团建游产品核心处理类 */@Servicepublic class TeambuildingCommonServiceImpl implements TeambuildingCommonService {    @Resource    private TeambuildingMapper teambuildingMapper;    @Override    public Page<TeambuildingEntity> pagingQuery(TeambildingPage pageParam) {        QueryWrapper<TeambuildingEntity> queryWrapper = new QueryWrapper<>();        return teambuildingMapper.selectPage(new Page<>(pageParam.getPage(), pageParam.getPageSize()), queryWrapper);    }    @Override    public IPage<TeambuildingBoEntity> coverTeambuildingEntityToBo(Page<TeambuildingEntity> pageEntity) {        return pageEntity.convert(entity -> ObjectMappingUtil.map(entity, TeambuildingBoEntity.class));    }}