package com.tsing.product.api.dict.service;import com.tsing.product.api.dict.bo.request.ProductDictSaveRequest;import com.tsing.product.api.dict.bo.request.ProductDictUpdateRequest;import org.junit.jupiter.api.Test;import org.springframework.boot.test.context.SpringBootTest;import java.util.Arrays;import java.util.List;import javax.annotation.Resource;import lombok.val;@SpringBootTestpublic class ProductDictServiceTest {    @Resource    private ProductDictService productDictService;    @Test    public void testSave() {        List<String> content = Arrays.asList(                "交通：往返大巴车、过路费、燃油费、停车费、司机餐补。",                "门票：青龙峡景区门票。",                "用餐：桌餐，不含酒水。",                "策划：主题活动策划及执行。",                "场地：活动场地使用费。",                "保险：全程意外保险。",                "教练：专业团建教练。",                "道具：活动道具费用。",                "领队：全程配备领队。",                "横幅：活动定制横幅。",                "用水：活动用水。"        );        ProductDictSaveRequest productDictSaveRequest = new ProductDictSaveRequest();        productDictSaveRequest.setCategoryCode("1000");        productDictSaveRequest.setCategoryDesc("分类描述");        productDictSaveRequest.setDictCode("1000");        productDictSaveRequest.setDictDesc("字典描述");        productDictSaveRequest.setSortNo(0);        productDictSaveRequest.setContent(content);        final val save = productDictService.save(productDictSaveRequest);        System.out.println(save);    }    @Test    public void testModify() {        ProductDictUpdateRequest productDictUpdateRequest = new ProductDictUpdateRequest();        productDictUpdateRequest.setId(1423047691379372034L);        productDictUpdateRequest.setCategoryCode("1001");        productDictUpdateRequest.setCategoryDesc("分类描述");        productDictUpdateRequest.setDictCode("1001");        productDictUpdateRequest.setDictDesc("字典描述");        productDictUpdateRequest.setSortNo(0);        productDictUpdateRequest.setDataType("STRING");        final val modify = productDictService.modify(productDictUpdateRequest);        System.out.println(modify);    }    @Test    public void testPageList() throws Exception {        final val resultPageDataResultData = productDictService.pageList("1000","1000");        System.out.println(resultPageDataResultData);    }}